function addToolbar(){const e=document.createElement("link");e.rel="stylesheet",e.href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.3.0/css/all.min.css",e.integrity="sha512-SzlrxWUlpfuzQ+pcUCosxcglQRNAq/DZjVsC0lE40xsADsfeQoEypE+enwcOiGjk/bSuGGKHEyjSoQ1zVisanQ==",e.crossOrigin="anonymous",document.head.appendChild(e);document.body.insertAdjacentHTML("beforeend",'\n      <div id="toolbar" style="position: fixed; bottom: 100px; right: 0;\n                           background: #f9fbfd;\n                           border: none;\n                           box-shadow: 0 4px 8px 3px rgba(0, 0, 0, .15), 0 1px 3px rgba(0, 0, 0, .3);\n                           border-radius: 28px;\n                           width: 40px;\n                           text-align: center;\n                           z-index: 99;\n                           ">\n        <div style="display: flex; flex-direction: column; padding: 8px 0;">\n          <button aria-label="Increase Text Size" class="tool-button" title="Increase Text Size" style="background: transparent; border: none; padding: 10px 0; font-size: 1.2rem; color: #4285f4; transition: all 0.3s ease-in-out; cursor: pointer; display: flex; align-items: center; justify-content: center; width: 100%;">\n            <i class="fa-solid fa-text-height" style="width: 24px; height: 24px;"></i>\n          </button>\n          <button aria-label="Increase Contrast" class="tool-button" title="Increase Contrast" style="background: transparent; border: none; padding: 10px 0; font-size: 1.2rem; color: #4285f4; transition: all 0.3s ease-in-out; cursor: pointer; display: flex; align-items: center; justify-content: center; width: 100%;">\n            <i class="fa-solid fa-adjust" style="width: 24px; height: 24px;"></i>\n          </button>\n          <button aria-label="Speak Page Aloud" class="tool-button" title="Speak Page Aloud" style="background: transparent; border: none; padding: 10px 0; font-size: 1.2rem; color: #4285f4; transition: all 0.3s ease-in-out; cursor: pointer; display: flex; align-items: center; justify-content: center; width: 100%;">\n            <div>\n              <i class="fa-solid fa-volume-up" style="width: 24px; height: 24px;"></i>\n            </div>\n          </button>\n          <button aria-label="Return to Top" class="tool-button" title="Return to Top" style="background: transparent; border: none; padding: 10px 0; font-size: 1.2rem; color: #4285f4; transition: all 0.3s ease-in-out; cursor: pointer; display: flex; align-items: center; justify-content: center; width: 100%;">\n            <i class="fa-solid fa-arrow-up" style="width: 24px; height: 24px;"></i>\n          </button>\n        </div>\n      </div>\n    ');const t=document.querySelector('[aria-label="Increase Text Size"]'),n=document.querySelector('[aria-label="Increase Contrast"]'),o=document.querySelector('[aria-label="Speak Page Aloud"]'),a=document.querySelector('[aria-label="Return to Top"]');let s=1;t.addEventListener("click",(()=>{1===s?(document.body.style.fontSize="140%",s=1.4,t.title="Reduce Text Size"):(document.body.style.fontSize="100%",s=1,t.title="Increase Text Size")}));let i=!1;n.addEventListener("click",(()=>{if(i){document.body.classList.remove("dark-mode"),i=!1,n.title="Increase Contrast";const e=document.head.querySelector("style");e&&document.head.removeChild(e)}else{document.body.classList.add("dark-mode"),i=!0,n.title="Decrease Contrast";const e=document.createElement("style");e.innerHTML="\n          body.dark-mode {\n            background-color: black;\n            color: white;\n          }\n        ",document.head.appendChild(e)}}));let l=!1;o.addEventListener("click",(()=>{if("speechSynthesis"in window)if(l)speechSynthesis.cancel(),l=!1,o.title="Read Text Aloud",o.querySelector("i:last-child").classList.replace("fa-pause","fa-volume-up");else{const e=new SpeechSynthesisUtterance;e.text=document.body.innerText,speechSynthesis.speak(e),l=!0,o.title="Stop",o.querySelector("i:last-child").classList.replace("fa-volume-up","fa-pause")}else alert("Your browser does not support text-to-speech.")})),a.addEventListener("click",(()=>{window.scrollTo({top:0,behavior:"smooth"})}))}addToolbar();
